// nc-chat loader (white-label) — acme.min.js
(function (w, d) {
  const cfg = {
    projectID: "VF_PROJECT_ID",            // <- pakeisi kai turėsi Voiceflow projektą
    lang: d.documentElement.lang || "lt",
    workerBase: "https://vf-proxy.emilijaemilija138.workers.dev" // <- pakeisi į savo workers.dev bazę
  };

  // Kraunam web widget bundle per tavo proxy (nematysis Voiceflow domenų)
  const s = d.createElement("script");
  s.type = "module";
  s.src = cfg.workerBase + "/vf/bundle.mjs";

  s.onload = () => {
    if (w.voiceflow && w.voiceflow.chat) {
      w.voiceflow.chat.load({
        verify:   { projectID: cfg.projectID },
        url:       cfg.workerBase + "/vf-runtime",
        versionID: "production",
        language:  cfg.lang
      });
    } else {
      console.warn("[Nova Concierge] Bundle įkeltas. Įrašyk tikrą VF ProjectID ir WORKER URL.");
    }
  };

  d.head.appendChild(s);
})(window, document);
