// nc-chat loader (white-label) — acme.min.js
(function(w,d){
  const cfg = {
    projectID: "ĮRAŠYK_VOICEFLOW_PROJECT_ID", // tavo VF projekto ID šitam klientui
    lang: d.documentElement.lang || "lt",
    // tavo proxy URL'ai (paslėps Voiceflow):
    runtimeURL: "https://api.novaconcierge.online/vf-runtime",
    bundleURL:  "https://api.novaconcierge.online/vf/bundle.mjs"
  };
  const s = d.createElement("script");
  s.type = "module";
  s.src  = cfg.bundleURL;
  s.onload = () => {
    if (w.voiceflow && w.voiceflow.chat) {
      w.voiceflow.chat.load({
        verify:   { projectID: cfg.projectID },
        url:       cfg.runtimeURL,
        versionID: "production",
        language:  cfg.lang
      });
    }
  };
  d.head.appendChild(s);
})(window, document);
